import Board from "./components/Board";
import { useState } from "react";
import "./App.css";

function App() {
  const [t, setT] = useState(Array(9).fill(null));
  const [turn, setTurn] = useState(P);
  const handletileclick = (i)=>{
    if(t[i]!==null){
      return;
    }
    const newtile={...t};
    newtile[i]=turn;
    setT(newtile);
    if(turn ==="X"){
      setTurn("O");
    }
    else{
      setTurn("X");
    }
  };

  return (
    <div>
      <h2>Tic-tac-toe</h2>
      <Board t={t} onTileCick={handletileclick}/>
    </div>
  );
}

export default App;
