import Board from "./components/Board";
import { useState , useEffect} from "react";
import "./App.css";

const Player_x="X";
const Player_o="O";
const arr=[
  {c: [0,1,2], d: "strike-row-1"},
  {c: [3,4,5], d: "strike-row-2"},
  {c: [6,7,8], d: "strike-row-3"},
  {c: [0,3,6], d: "strike-col-1"},
  {c: [1,4,7], d: "strike-col-2"},
  {c: [2,5,8], d: "strike-col-3"},
  {c: [0,4,8], d: "strike-diag-1"},
  {c: [2,4,6], d: "strike-diag-2"},
];

function win(t,setD){}

function App() {
  const [t, setT] = useState(Array(9).fill(null));
  const [turn, setTurn] = useState("X");
  const [d, setD]=useState();

  const handletileclick = (i)=>{
    if(t[i]!==null){
      return;
    }
    const newtile={...t};
    newtile[i]=turn;
    setT(newtile);
    if(turn === "X"){
      setTurn("O");
    }
    else{
      setTurn("X");
    }
  };

  useEffect(()=>{
    win(t,setD)
  },[t]);

  return (
    <div>
      <h2>Tic-tac-toe</h2>
      <Board turn={turn}
             t={t} 
             onTileCick={handletileclick}
             d={d}/>
    </div>
  );
}

export default App;
