import Board from "./components/Board";
import { useState , useEffect} from "react";
import "./App.css";

const Player_x="X";
const Player_o="O";

function App() {
  const [t, setT] = useState(Array(9).fill(null));
  const [turn, setTurn] = useState("X");

  const handletileclick = (i)=>{
    if(t[i]!==null){
      return;
    }
    const newtile={...t};
    newtile[i]=turn;
    setT(newtile);
    if(turn === "X"){
      setTurn("O");
    }
    else{
      setTurn("X");
    }
  };

  useEffect(()=>{
    window(t)
  },[t]);

  return (
    <div>
      <h2>Tic-tac-toe</h2>
      <Board turn={turn}
             t={t} 
             onTileCick={handletileclick}/>
    </div>
  );
}

export default App;
