function App() {
  const [t, setT] = useState(Array(9).fill(null));
  const [turn, setTurn] = useState("X");
  const [state, setState] = useState(GameState.inProgress);
  const [d, setD]=useState();

  const handletileclick = (i)=>{
    if(state!==GameState.inProgress){
      return;
    }

    if(t[i]!==null){
      return;
    }
    const newtile={...t};
    newtile[i]=turn;
    setT(newtile);
    if(turn === "X"){
      setTurn("O");
    }
    else{
      setTurn("X");
    }
  };

  useEffect(()=>{
    win(t,setD,setState)
  },[t]);

  return (
    <div>
      <h2>Tic-tac-toe</h2>
      <Board turn={turn}
             t={t} 
             onTileCick={handletileclick}
             d={d}/>
      <GameDone state={state}/>       

    </div>
  );
}

export default App;
