import { useState, useEffect } from "react";
import Board from "./Board";
// import GameDone from "./GameDone";
import GameState from "./GameState";
import Reset from "./Reset";
import gameOverSoundAsset from "../sounds/game_over.wav";
import clickSoundAsset from "../sounds/click.wav";
import GameDone from "./GameDone";

const gameOverSound = new Audio(gameOverSoundAsset);
gameOverSound.volume = 0.2;
const clickSound = new Audio(clickSoundAsset);
clickSound.volume = 0.5;

const PLAYER_X = "X";
const PLAYER_O = "O";

const arr = [
  //Rows
  { c: [0, 1, 2], d: "strike-row-1" },
  { c: [3, 4, 5], d: "strike-row-2" },
  { c: [6, 7, 8], d: "strike-row-3" },

  //Columns
  { c: [0, 3, 6], d: "strike-column-1" },
  { c: [1, 4, 7], d: "strike-column-2" },
  { c: [2, 5, 8], d: "strike-column-3" },

  //Diagonals
  { c: [0, 4, 8], d: "strike-diagonal-1" },
  { c: [2, 4, 6], d: "strike-diagonal-2" },
];

function checkWinner(t, setD, setState) {
  for (const { c, d } of arr) {
    const tileValue1 = t[c[0]];
    const tileValue2 = t[c[1]];
    const tileValue3 = t[c[2]];

    if (
      tileValue1 !== null &&
      tileValue1 === tileValue2 &&
      tileValue1 === tileValue3
    ) {
      setD(d);
      if (tileValue1 === PLAYER_X) {
        setState(GameState.playerXWins);
      } else {
        setState(GameState.playerOWins);
      }
      return;
    }
  }

  const areAllTilesFilledIn = t.every((tile) => tile !== null);
  if (areAllTilesFilledIn) {
    setState(GameState.draw);
  }
}
 


function App1() {
  const [t, setT] = useState(Array(9).fill(null));
  const [turn, setTurn] = useState(PLAYER_X);
  const [d, setD] = useState();
  const [state, setState] = useState(GameState.inProgress);

  const handleTileClick = (i) => {
    if (state !== GameState.inProgress) {
      return;
    }

    if (t[i] !== null) {
      return;
    }
    const newtile={...t};
    newtile[i]=turn;
    setT(newtile);
    if(turn === PLAYER_X){
      setTurn(PLAYER_O);
    }
    else{
      setTurn(PLAYER_X);
    }
  };
    

  const handleReset = () => {
    setState(GameState.inProgress);
    setT(Array(9).fill(null));
    setTurn(PLAYER_X);
    setD(null);
  };

  useEffect(() => {
    checkWinner(t, setD, setState);
  }, [t]);

  useEffect(() => {
    if (t.some((tile) => tile !== null)) {
      clickSound.play();
    }
  }, [t]);

  useEffect(() => {
    if (state !== GameState.inProgress) {
      gameOverSound.play();
    }
  }, [state]);

  return (
    <div>
      <h1>Tic Tac Toe</h1>
      <Board
        turn={turn}
        t={t}
        onTileClick={handleTileClick}
        d={d}
      />
      <GameDone state={state} />
      <Reset state={state} onReset={handleReset} />
    </div>
  );
}

export default App1;
