import Board from "./components/Board";
import { useState , useEffect} from "react";
import "./App.css";

const Player_x="X";
const Player_o="O";
const arr=[
  {c: [0,1,2], d: "strike-row"}
]

function App() {
  const [t, setT] = useState(Array(9).fill(null));
  const [turn, setTurn] = useState("X");
  const [d, setD]=useState();

  const handletileclick = (i)=>{
    if(t[i]!==null){
      return;
    }
    const newtile={...t};
    newtile[i]=turn;
    setT(newtile);
    if(turn === "X"){
      setTurn("O");
    }
    else{
      setTurn("X");
    }
  };

  useEffect(()=>{
    win(t,setD)
  },[t]);

  return (
    <div>
      <h2>Tic-tac-toe</h2>
      <Board turn={turn}
             t={t} 
             onTileCick={handletileclick}
             d={d}/>
    </div>
  );
}

export default App;
